module atcrambrg200 (
	  hclk,
	  hresetn,
	  hsel,
	  hready,
	  haddr,
	  hwrite,
	  htrans,
	  hsize,
	  hburst,
	  hprot,
	  hreadyout,
	  hresp,
	  mem_csb,
	  mem_web,
	  mem_addr
);
parameter		DATA_WIDTH	= 32;
parameter		MIN_WDATA_WIDTH	= 8;
parameter		ADDR_WIDTH	= 20;
parameter	[63:0]	MEM_SIZE_KB	= (1 << (ADDR_WIDTH-10));
parameter		OOR_ERR_EN	= 1;

localparam		MEM_ADDR_LSB	= $clog2({32'b0, DATA_WIDTH/8});
localparam 		MEM_ADDR_MSB    = $clog2(MEM_SIZE_KB) - 1 + 10;
localparam		MEM_ADDR_WIDTH  = MEM_ADDR_MSB-MEM_ADDR_LSB+1;
localparam		MEM_SPACE_KB	= MEM_SIZE_KB - 1;
localparam		WSTRB_WIDTH	= (DATA_WIDTH/8);
localparam		HSIZE_D1_WIDTH	= (DATA_WIDTH >= 128) ? 3 : 2;
localparam		HTRANS_NONSEQ	= 2'h2;
localparam		HTRANS_SEQ	= 2'h3;
localparam		S_IDLE		= 2'h0;
localparam		S_RD_DATA	= 2'h0;
localparam		S_WR		= 2'h1;
localparam		S_RAW_WAIT	= 2'h2;
localparam		S_ERROR_1	= 2'h3;
localparam		S_ERROR_2	= 2'h0;

localparam 		PRODUCT_ID	= 32'h000b2002;

input						hclk;
input						hresetn;
input						hsel;
input						hready;
input	[ADDR_WIDTH-1:0]			haddr;
input						hwrite;
input	[1:0]					htrans;
input	[2:0]					hsize;
input	[2:0]					hburst;
input	[3:0]					hprot;
output						hreadyout;
output						hresp;

output						mem_csb;
output	[(DATA_WIDTH/8)-1:0]			mem_web;
output	[MEM_ADDR_WIDTH-1:0]			mem_addr;

reg	[WSTRB_WIDTH-1:0]			wstrb_lookup;
reg	[MEM_ADDR_MSB:0]			haddr_d1;
reg	[1:0]					brg_cs;
reg	[1:0]					brg_ns;
reg	[HSIZE_D1_WIDTH-1:0]			hsize_d1;
reg						cs_error_2;
reg						valid_wr_dp;
reg						haddr_hit_mem_d1;
wire	[HSIZE_D1_WIDTH-1:0]			hsize_nx;
wire	[MEM_ADDR_MSB:0]			mem_addr_all_bit;
wire						haddr_hit_mem;
wire						valid_ap;
wire						valid_rd_ap;
wire						valid_wr_ap;
wire						cs_error_1;
wire						cs_raw_wait;
wire						cs_wr;
wire						haddr_invalid;

assign cs_raw_wait	=   brg_cs   == S_RAW_WAIT;
assign cs_error_1 	=   brg_cs   == S_ERROR_1;
assign cs_wr		=   brg_cs   == S_WR;
assign valid_ap		=   hready   &&    hsel  &&  ((htrans == HTRANS_NONSEQ) || ( htrans == HTRANS_SEQ));
assign valid_wr_ap	=   valid_ap &&    hwrite;
assign valid_rd_ap	=   valid_ap &&  (!hwrite);
assign hreadyout	= !(cs_error_1  || cs_raw_wait);
assign hresp		=  (cs_error_1  || cs_error_2 );
assign hsize_nx		=   valid_ap              ? hsize[HSIZE_D1_WIDTH-1:0]   : hsize_d1;
generate
	if (DATA_WIDTH != MIN_WDATA_WIDTH)
	begin : gen_mem_wb_for_diff_DATA_WIDTH_and_MIN_WDATA_WIDTH
		assign mem_web		=   valid_wr_dp           ? wstrb_lookup : {WSTRB_WIDTH{1'b1}};
	end
	else if (DATA_WIDTH == MIN_WDATA_WIDTH)
	begin : gen_mem_wb_for_same_DATA_WIDTH_and_MIN_WDATA_WIDTH
		assign mem_web		=   valid_wr_dp           ? {WSTRB_WIDTH{1'b0}} : {WSTRB_WIDTH{1'b1}};
	end
endgenerate
assign mem_addr_all_bit	=  (cs_raw_wait || cs_wr) ? haddr_d1     : haddr[MEM_ADDR_MSB:0];
assign mem_csb		= !((valid_rd_ap && haddr_hit_mem) || valid_wr_dp || (cs_raw_wait && haddr_hit_mem_d1));
assign mem_addr		=   mem_addr_all_bit[MEM_ADDR_MSB:MEM_ADDR_LSB];

always @(posedge hclk or negedge hresetn) begin
	if (~hresetn)
		brg_cs			<= S_IDLE;
	else
		brg_cs			<= brg_ns;
end

always @(posedge hclk or negedge hresetn) begin
	if (~hresetn) begin
		hsize_d1		<= {HSIZE_D1_WIDTH{1'b0}};
		cs_error_2		<= 1'b0;
		haddr_hit_mem_d1	<= 1'b0;
	end
	else begin
		hsize_d1		<= hsize_nx;
		cs_error_2		<= cs_error_1;
		haddr_hit_mem_d1	<= haddr_hit_mem;
	end
end
always @(posedge hclk or negedge hresetn) begin
	if (~hresetn) begin
		haddr_d1	<= {(MEM_ADDR_MSB+1){1'b0}};
		valid_wr_dp	<= 1'b0;
	end
	else if (hready) begin
		haddr_d1	<= haddr[MEM_ADDR_MSB:0];
		valid_wr_dp	<= valid_wr_ap && haddr_hit_mem;
	end
end

always @(*) begin
        case(brg_cs)
                S_ERROR_1:
                        brg_ns = S_ERROR_2;
                S_RAW_WAIT:
                        brg_ns = S_RD_DATA;
                S_WR:
                        if (haddr_invalid)
                                brg_ns = S_ERROR_1;
                        else if (valid_rd_ap)
                                brg_ns = S_RAW_WAIT;
                        else if (valid_wr_ap && haddr_hit_mem)
                                brg_ns = S_WR;
                        else
                                brg_ns = S_IDLE;
                default:
                        if (haddr_invalid)
                                brg_ns = S_ERROR_1;
                        else if (valid_wr_ap && haddr_hit_mem)
                                brg_ns = S_WR;
                        else
                                brg_ns = S_IDLE;
        endcase
end

